import { useQuery } from '@tanstack/react-query'

import { fetchExtended } from '@/configs/fetch/fetch-extend'

import { ErrorResponseDTOCommonType } from '../@types/data-contracts'
import {
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { CategoryApiApi } from './CategoryApi.api'

export const categoryApiApi = new CategoryApiApi({
  customFetch: fetchExtended,
})

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */

export const QUERY_KEY_CATEGORY_API_API = {
  GET_CATEGORIES: (
    variables?: Parameter<typeof categoryApiApi.getCategories>,
  ) => ['GET_CATEGORIES', variables].filter(isDefined),
}

/**
 * @description ## 팝업 카테고리 전체 조회 - DB에 저장되어 있는 카테고리 전체 조회 - Popup 전체 조회 시, CategoryId 값으로 활용
 *
 * @tags [Category API]
 * @name GetCategories
 * @summary 팝업 카테고리 전체 조회
 * @request GET:/v1/category
 * @secure    */

export const useGetCategoriesQuery = <
  TData = RequestFnReturn<typeof categoryApiApi.getCategories>,
>(
  params?: QueryHookParams<
    typeof categoryApiApi.getCategories,
    {
      error:
        | ErrorResponseDTOCommonType
        | {
            /** @example "UNAUTHORIZED_ACCESS" */
            errorCode?: string
            /** @example "인증이 필요한 접근입니다" */
            description?: string
          }
        | {
            /** @example "ACCESS_DENIED" */
            errorCode?: string
            /** @example "접근이 거부되었습니다" */
            description?: string
          }
    },
    TData
  >,
) => {
  const queryKey = QUERY_KEY_CATEGORY_API_API.GET_CATEGORIES(params?.variables)
  return useQuery({
    queryKey,
    queryFn: () => categoryApiApi.getCategories(params?.variables),
    ...params?.options,
  })
}

