import { useMutation } from '@tanstack/react-query'

import { fetchExtended } from '@/configs/fetch/fetch-extend'

import { ErrorResponseDTOCommonType } from '../@types/data-contracts'
import { MutationHookParams } from '../@types/react-query-type'
import { PresignedApiApi } from './PresignedApi.api'

export const presignedApiApi = new PresignedApiApi({
  customFetch: fetchExtended,
})

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */

export const QUERY_KEY_PRESIGNED_API_API = {
  CREATE_PRESIGNED_URL: () => ['CREATE_PRESIGNED_URL'],
}

/**
 * @description ## Presigned URL 발급 API 1. 해당 API 호출 시, 미리 서명된 URL을 발급해 반환 - method: POST - url: /v1/presigned_url/ - body: PresignedRequestDto (파일명, 파일 타입, 파일 경로 정보 포함) - 반환값: PresignedResponseDto (발급된 URL 및 필드 정보 포함) 2. 반환된 URL로 파일 업로드 - method: PUT - url: [발급된 URL] - body: 업로드할 파일 (binary / file 형식으로 전송) ### 주의사항 ⚠️ Spring 서버의 Presigned URL API는 기존 Django 서버와의 동작 방식에 차이가 있으므로, <b>프론트엔드 코드에 수정이 필요합니다!</b> ⚠️ - Spring 서버 (S3 Presigned URL 사용): - PUT 메서드 사용 - Content-Type을 파일 MIME 타입으로 설정 - 본문에 바이너리 데이터 직접 전송 - multipart/form-data 사용 ❌
 *
 * @tags [Presigned API]
 * @name CreatePresignedUrl
 * @summary 미리 서명된 URL 발급
 * @request POST:/v1/presigned_url/
 * @secure  */

export const useCreatePresignedUrlMutation = (
  params: MutationHookParams<
    typeof presignedApiApi.createPresignedUrl,
    {
      error:
        | ErrorResponseDTOCommonType
        | {
            /** @example "UNAUTHORIZED_ACCESS" */
            errorCode?: string
            /** @example "인증이 필요한 접근입니다" */
            description?: string
          }
        | {
            /** @example "ACCESS_DENIED" */
            errorCode?: string
            /** @example "접근이 거부되었습니다" */
            description?: string
          }
    }
  >,
) => {
  const mutationKey = QUERY_KEY_PRESIGNED_API_API.CREATE_PRESIGNED_URL()
  return useMutation({
    mutationKey,
    mutationFn: presignedApiApi.createPresignedUrl,
    ...params?.options,
  })
}

